.tmap-flash-message {
  border: 1px solid lightgray;
  padding: 5px;
  margin: 6px 0;
  display: block;
  color: #333333; }
  .tmap-flash-message.tmap-info, .tmap-flash-message.tmap-plain, .tmap-flash-message.tmap-neutral {
    background: #F0F0F0; }
    .tmap-flash-message.tmap-info::before, .tmap-flash-message.tmap-plain::before, .tmap-flash-message.tmap-neutral::before {
      content: "i";
      display: inline-block;
      border-radius: 50%;
      width: 0.8em;
      border: 2px solid;
      font-family: serif;
      text-align: center;
      height: 0.8em;
      font-weight: bold;
      line-height: 0.8em;
      vertical-align: text-top;
      margin-right: 2px; }
  .tmap-flash-message.tmap-valid, .tmap-flash-message.tmap-success {
    background: #D1EED4; }
    .tmap-flash-message.tmap-valid::before, .tmap-flash-message.tmap-success::before {
      content: "i";
      display: inline-block;
      border-radius: 50%;
      width: 0.8em;
      border: 2px solid;
      font-family: serif;
      text-align: center;
      height: 0.8em;
      font-weight: bold;
      line-height: 0.8em;
      vertical-align: text-top;
      margin-right: 2px; }
  .tmap-flash-message.tmap-warning {
    background: #FFF6D1; }
    .tmap-flash-message.tmap-warning::before {
      content: "";
      display: inline-block;
