  </$button>
</td>
<td>
  <$button tooltip="Create outgoing edge">
    <<tmap "option" "misc.arrows.out">>
    <$action-sendmessage $message="tmap:tm-create-edge"
                         from=<<currentTiddler>>
                         to=<<item>>
                         force="true"
                         label={{$:/temp/quickConnectSearch/type}}
